import React from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import AutoSizer from "react-virtualized-auto-sizer";
import * as d3 from 'd3';

import { countries } from './countries.js';
import { getTimeseriesData } from './rest_api.js';

import Plot from 'react-plotly.js';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			country: "US",
			metric: "confirmed",
			startDate: new Date(),
			endDate: new Date(),

			timeseriesData:[],
		}

		this.onPlot = this.onPlot.bind(this);
	}

	async onPlot() {
		const { country, metric, startDate, endDate } = this.state;

		// Declare variables.
		let timeseriesData = [];
		let countryTotalConfirmed = 0;
		let countryTotalRecovered = 0;
		let countryTotalDeaths = 0;

		// Get data.
		let data = await getTimeseriesData(country);
		if(data.data && data.data.length > 0) {
			data = data.data[0] && data.data[0].timeseries ? data.data[0].timeseries : {};

			var parseTime = d3.timeParse("%-m/%-d/%y");
			Object.keys(data).forEach((key) => {
				let date = parseTime(key);
				if(date >= startDate && date <= endDate) {
					timeseriesData.push({date:date, value:data[key][metric]});
				}

				countryTotalConfirmed = countryTotalConfirmed + data[key].confirmed;
				countryTotalRecovered = countryTotalRecovered + data[key].recovered;
				countryTotalDeaths = countryTotalDead + data[key].deaths;
			});
		}

		this.setState({
			timeseriesData: timeseriesData,
			countryTotalConfirmed: countryTotalConfirmed,
			countryTotalRecovered: countryTotalRecovered,
			countryTotalDeaths: countryTotalDeaths,
		})
	}

	render() {
		const { country, metric, startDate, endDate, timeseriesData } = this.state;

		return (
			<div className="d-flex flex-column" style={{height:"100vh", width:"100vw"}}>
				<header>
				</header>
				<div className="flex-grow-1 d-flex p-3">
					<div className="card flex-grow-1 mr-3">
						<div className="card-body d-flex flex-column">
							<Form
								country={country}
								metric={metric}
								startDate={startDate}
								endDate={endDate}

								onCountryChange={(e) => this.setState({country:e.target.value})}
								onMetricChange={(e) => this.setState({metric:e.target.value})}
								onStartDateChange={(val) => this.setState({startDate:val})}
								onEndDateChange={(val) => this.setState({endDate:val})}

								onPlot={this.onPlot}
							/>
							<Graph
								metric={metric}
								timeseriesData={timeseriesData}
							/>
						</div>
					</div>
					<div className="card">
						<div className="card-body">Basic card</div>
					</div>
				</div>
				<footer>
				</footer>
			</div>
		);
	}
}

function Form (props) {
	return (
		<form className="form-inline">
			<label>Compare:</label>
			<select
				className="form-control" 
				value={props.country}
				onChange={props.onCountryChange}
			>
			{countries.map((country, i) => {
				return (
					<option key={i} value={country.Code}>{country.Name}</option>
				);
			})}
			</select>
			<label>VS</label>
			<select
				className="form-control" 
				onChange={props.onMetricChange}
				value={props.metric}
			>
				<option value="confirmed">Total confirmed cases</option>
				<option value="recovered">Total recovered</option>
				<option value="deaths">Total dead</option>
			</select>
			<DatePicker
				onChange={props.onStartDateChange}
				selected={props.startDate}
			/>
			<label>-</label>
			<DatePicker
				onChange={props.onEndDateChange}
				selected={props.endDate}
			/>
			<button 
				className="btn"
				onClick={(e) => {
					e.preventDefault();
					props.onPlot();
				}}
			>
				Submit
			</button>
		</form>
	);
}

function Graph (props) {
	const { timeseriesData, metric } = props;
	let color = "red";

	return (
		<div className="flex-grow-1" style={{position:"relative"}}>
			<AutoSizer>
				{({ height, width }) => (
					<Plot
						config={{displayModeBar: false}}
						data=
						{
							[
								{
									x: timeseriesData.map((data) => data.date),
									y: timeseriesData.map((data) => data.value),
									mode: 'lines',
									marker: {color: color},
								},
							]
						}
						layout={ {width: width, height: height} }
					/>
				)}
			</AutoSizer>
		</div>
	);
}

export default App;
