import React from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import { countries } from './countries.js';

import Metrics from './metrics.js';

import { getTimeseriesData } from './rest_api.js';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			country: "US",
			metric: "confirmed",
			startDate: null,
			endDate: null,
		}

		this.onPlot = this.onPlot.bind(this);
	}

	async onPlot() {
		const { country, metric, startDate, endDate } = this.state;

		let data = await getTimeseriesData(country);
		console.log(data);
	}

	render() {
		const { country, metric, startDate, endDate } = this.state;

		return (
			<div className="d-flex flex-column" style={{height:"100vh", width:"100vw"}}>
				<header>
				</header>
				<div className="flex-grow-1 d-flex p-3">
					<div className="card flex-grow-1 mr-3">
						<div className="card-body d-flex flex-column">
							<Form
								country={country}
								metric={metric}
								startDate={startDate}
								endDate={endDate}

								onCountryChange={(e) => this.setState({country:e.target.value})}
								onMetricChange={(e) => this.setState({metric:e.target.value})}
								onStartDateChange={(val) => this.setState({startDate:val})}
								onEndDateChange={(val) => this.setState({endDate:val})}

								onPlot={this.onPlot}
							/>
							<Graph
							/>
						</div>
					</div>
					<div className="card">
						<div className="card-body">Basic card</div>
					</div>
				</div>
				<footer>
				</footer>
			</div>
		);
	}
}

function Form (props) {
	return (
		<form className="form-inline">
			<label>Compare:</label>
			<select
				className="form-control" 
				value={props.country}
				onChange={props.onCountryChange}
			>
			{countries.map((country, i) => {
				return (
					<option key={i} value={country.Code}>{country.Name}</option>
				);
			})}
			</select>
			<label>VS</label>
			<select
				className="form-control" 
				onChange={props.onMetricChange}
				value={props.metric}
			>
				<option value="confirmed">Total confirmed cases</option>
				<option value="recovered">Total recovered</option>
				<option value="dead">Total dead</option>
			</select>
			<DatePicker
				onChange={props.onStartDateChange}
				selected={props.startDate}
			/>
			<label>-</label>
			<DatePicker
				onChange={props.onEndDateChange}
				selected={props.endDate}
			/>
			<button 
				className="btn"
				onClick={(e) => {
					e.preventDefault();
					props.onPlot();
				}}
			>
				Submit
			</button>
		</form>
	);
}

function Graph (props) {
	return (
		<div className="flex-grow-1">
		</div>
	);
}

export default App;
