import React from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Countries from './countries.js';
import Metrics from './metrics.js';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			country: null,
			metric: null,
			startDate: null,
			endDate: null,
		}
	}

	render() {
		const { country, metric, startDate, endDate } = this.state;

		return (
			<div className="d-flex flex-column" style={{height:"100vh", width:"100vw"}}>
				<header>
				</header>
				<div className="flex-grow-1 d-flex p-3">
					<div class="card flex-grow-1 mr-3">
						<div class="card-body d-flex flex-column">
							<Form
								country={country}
								metric={metric}
								startDate={startDate}
								endDate={endDate}

								onCountryChange={(e) => this.setState({country:e.target.value})}
								onMetricChange={(e) => this.setState({metric:e.target.value})}
								onStartDateChange={(val) => this.setState({startDate:val})}
								onEndDateChange={(val) => this.setState({endDate:val})}

								onPlot={this.onPlot}
							/>
							<Graph
							/>
						</div>
					</div>
					<div class="card">
						<div class="card-body">Basic card</div>
					</div>
				</div>
				<footer>
				</footer>
			</div>
		);
	}
}

function Form (props) {
	return (
		<form className="form-inline">
			<label>Compare:</label>
			<Countries 
				country={props.country}
				onCountryChange={props.onCountryChange}
			/>
			<label>VS</label>
			<Metrics
				metric={props.metric}
				onMetricChange={props.onMetricChange}
			/>
			<DatePicker
				name="startDate"
				onChange={props.onStartDateChange}
				selected={props.startDate}
			/>
			<label>-</label>
			<DatePicker
				name="endDate"
				onChange={props.onEndDateChange}
				selected={props.endDate}
			/>
			<button 
				className="btn"
				onClick={props.onPlot}
			>
				Submit
			</button>
		</form>
	);
}

function Graph (props) {
	return (
		<div className="flex-grow-1">
		</div>
	);
}

export default App;
