import React from 'react';
import * as d3 from 'd3';

class Chart extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			marginTop: 10, 
			marginRight:25,  
			marginBottom:25, 
			marignLeft: 50
		}
	}

	componentDidUpdate() {
		document.querySelectorAll(".textpoint").forEach((elem) => {
			console.log(elem);

		});
	}

	render() {
		const { timeseriesData, measuresData, height, width } = this.props;

		// Margins.
		const marginTop = 10;
		const marginRight = 25; 
		const marginBottom = 25; 
		const marginLeft = 50;

		// Find min & max values.
		let minX = Math.min(d3.min((timeseriesData) => timeseriesData.value), d3.min((measuresData) => timeseriesData.value))
		let maxX = Math.max(d3.min((measuresData) => timeseriesData.value), d3.min((measuresData) => timeseriesData.value))

		const xScale = d3.scaleTime()
			.domain([minX, maxX])
			.range([marginLeft, width - marginRight]);

			console.log(minX + ' ' + maxX);

		const yScale = d3.scaleLinear()
			.domain([d3.min((timeseriesData) => timeseriesData.value), d3.max((timeseriesData) => timeseriesData.value)])
			.range([height - marginBottom, marginTop]);

		return (
			<svg style={{height:`${height}px`, width:`${width}px`}}>
				<LineChart
					xScale={xScale}
					yScale={yScale}
					data={timeseriesData}
				/>
			</svg>
		);
	}
}



function LineChart(props) {
	// Get props.
	const { xScale, yScale, data } = props;

	console.log(data);

	// Build line function.
	var line = d3.line()
		.x(function(d) { return xScale(d.date); })
		.y(function(d) { return yScale(d.value); });

	var linePlot = line(data);

	// Plot.
	return (
		<path 
			className="line" 
			d={linePlot} 
			style={{fill:'none', strokeWidth:'1.5px', stroke:props.stroke}}
		/>
	);
}

export default Chart;
