import React from 'react';
import * as d3 from 'd3';
import uuidv1 from "uuid";

class Chart extends React.Component {
	constructor(props) {
		super(props);
		this.uuid = `_${uuidv1()}`;
	}

	componentDidUpdate() {
		document.querySelectorAll(".textpoint").forEach((elem) => {
			console.log(elem);

		});
	}

	render() {
		const { timeseriesData, measuresData, height, width } = this.props;

		// Margins.
		const marginTop = 10;
		const marginRight = 25; 
		const marginBottom = 25; 
		const marginLeft = 50;

		// Find min & max values.
		let minX = Math.min(d3.min(timeseriesData, function(d) { return d.date; }), d3.min(measuresData, function(d) { return d.date_implemented; }))
		let maxX = Math.max(d3.max(timeseriesData, function(d) { return d.date; }), d3.max(measuresData, function(d) { return d.date_implemented; }))
		let minY = d3.min(timeseriesData, function(d) { return d.value; });
		let maxY = d3.max(timeseriesData, function(d) { return d.value; });

		// Build X & Y scale.
		const xScale = d3.scaleTime()
			.domain([minX, maxX])
			.range([marginLeft, width - marginRight]);

		const yScale = d3.scaleLinear()
			.domain([minY, maxY])
			.range([height - marginBottom, marginTop]);

		return (
			<svg style={{height:`${height}px`, width:`${width}px`}}>
				<defs>
		            <clipPath id={this.uuid}>
		                <rect x={marginLeft} y={marginTop} width={width - marginLeft - marginRight} height={height - marginTop - marginBottom}/>
		            </clipPath>
		        </defs>
				<g clipPath={`url(#${this.uuid})`}>
					<LineChart
						xScale={xScale}
						yScale={yScale}
						data={timeseriesData}
					/>
				</g>
			</svg>
		);
	}
}

function LineChart(props) {
	// Get props.
	const { xScale, yScale, data } = props;

	// Build line function.
	var line = d3.line()
		.x(function(d) { return xScale(d.date); })
		.y(function(d) { return yScale(d.value); });

	var linePlot = line(data);

	// Plot.
	return (
		<path 
			className="line" 
			d={linePlot} 
			style={{fill:'none', strokeWidth:'1.5px', stroke:"red"}}
		/>
	);
}

export default Chart;
